import React from 'react';

import { Link } from 'components/widgets';

export default ({ id, name }) => (
  <Fragment>
    <Link href={`//discogs.com/artist/${id}`} title={name}>
      {name}
    </Link>
    {!last && <span> / </span>}
  </Fragment>
);
